// import './CharacterCard.css'

import React, {Component} from 'react'

class NowPlayingCard extends Component {
    constructor(props){
        super(props)
        // console.log(this.props);

        this.state = {verDesc: false}
    }
    handleverDesc(){
        this.setState({
            verDesc: !this.state.verDesc
          })
    }
    render(){
        // console.log(this.props);

        const { poster_path, title, overview } = this.props.pelicula;
        return(
        <article>
            <img src={poster_path} alt="" />
            <h4> {title} </h4> 

            <p className={ this.state.verDesc ? "show" : "hide" }>{overview}</p> 
            <p className = "more" onClick={()=> this.handleverDesc()}>{this.state.verDesc ? "Ocultar Descripcion" : "Ver Descripcion"}</p>

        </article>

        );
    }

}

export default NowPlayingCard
